#!/usr/bin/env node

const { program } = require('commander');

program
  .version('1.0.0')
  .description('Uma CLI simples em JavaScript');

program
  .command('  Bem Vindo  <name>')
  .description('Exibe uma saudação para o usuário')
  .action((name) => {
    console.log(`Olá, ${name}!`);
  });

program.parse(process.argv);